void init_menu0C_missionfailed(void)
{
  tab_next_selected = FALSE;
  tab_prev_selected = FALSE;
  load_walletbond();
  load_briefing_text_for_stage();
  set_cursor_pos_tab2();
  if (maybe_is_in_menu != FALSE) {
    sndApplyVolumeAllSfxSlot(0x7fff);
    musicTrack1ApplySeqpVol(0x7fff);
    g_musicXTrack1Fade = MUSIC_FADESTATE_UNSET;
    musicTrack1Play(M_FOLDERS);
    maybe_is_in_menu = FALSE;
  }
}

void update_menu0C_missionfailed(void) {
    return;
}


void interface_menu0C_missionfailed(void)
{
    viSetFovY(60.0f);
    viSetAspect(1.3333334f);
    viSetZRange(100.0f, 10000.0f);
    viSetUseZBuf(FALSE);
    tab_next_highlight = FALSE;
    tab_prev_highlight = FALSE;
    if (isontab3())
    {
        tab_prev_highlight = TRUE;
    }
    else
    {
        if (isontab2())
        {
            tab_next_highlight = TRUE;
        }
        else
        {
            tab_next_highlight = TRUE;
        }
    }
    if (joyGetButtonsPressedThisFrame(PLAYER_1, START_BUTTON|Z_TRIG|A_BUTTON))
    {
        if (tab_prev_highlight)
        {
            tab_prev_selected = TRUE;
            sndPlaySfx(g_musicSfxBufferPtr, DOOR_METAL_CLOSE2_SFX, 0);
        }
        else
        {
            if (tab_next_highlight)
            {
                tab_next_selected = TRUE;
                sndPlaySfx(g_musicSfxBufferPtr, DOOR_METAL_CLOSE2_SFX, 0);
            }
        }
    }
    else
    {
        if (joyGetButtonsPressedThisFrame(PLAYER_1, B_BUTTON))
        {
            tab_prev_selected = TRUE;
            sndPlaySfx(g_musicSfxBufferPtr, DOOR_METAL_CLOSE2_SFX, 0);
        }
    }
    disable_all_switches(walletinst[0]);
    set_item_visibility_in_objinstance(walletinst[0], 0, TRUE);
    set_item_visibility_in_objinstance(walletinst[0], 1, TRUE);
    set_item_visibility_in_objinstance(walletinst[0], 3, TRUE);
    set_item_visibility_in_objinstance(walletinst[0], 6, TRUE);
    menu_control_stick_tracking();
    if (tab_next_selected)
    {
        set_menu_to_mode(MENU_MISSION_COMPLETE, 0);
        return;
    }
    if (tab_prev_selected)
    {
        set_menu_to_mode(MENU_MISSION_SELECT, 0);
        set_cursor_to_stage_solo(mission_folder_setup_entries[briefingpage].mission_num);
    }
}







s32 frontCompleteAllObjectivesAliveSuccess(void)
{
    s32 i;

    if (mission_failed_or_aborted || g_isBondKIA)
    {
        return 0;
    }

    for (i=0; i<10; i++)
    {
        if (ptrbriefingdata->datas[i].difficulty_related != 0
            && lvlGetSelectedDifficulty() >= ptrbriefingdata->datas[i].selected_difficulty
            && get_status_of_objective(i) != OBJECTIVESTATUS_COMPLETE)
            {
                return 0;
            }
    }

    return 1;
}


Gfx * constructor_menu0C_missionfailed(Gfx *DL)
{
    s32 x;
    s32 y;

    char stagename[0xbb8];
    char *text;
    s32 phi_v1;

    s32 y2;
    s32 x2;


    DL = viSetFillColor(DL, 0, 0, 0);
    DL = viFillScreen(DL);
    #if defined(BUGFIX_R2)
    DL = viFillScreen(DL);
    DL = viFillScreen(DL);
    #endif
    DL = sub_GAME_7F00D5E8(DL);
    DL = microcode_constructor(DL);
    DL = print_current_solo_briefing_stage_name(DL, &stagename);

    text = langGet(TEXT(LTITLE, 0x62)); //REPORT:*
    x = 0x37;
    y = 0x8F;
    DL = write_text_at_abs_coord(DL, &x, &y, text, ptrSecondFontTableLarge, ptrFirstFontTableLarge, 0xFF, viGetX(), viGetY(), 0, 0);

    text = langGet(TEXT(LTITLE, 0x63)); //Mission status:*
    x2 = 0;
    y2 = 0;
    sub_GAME_7F0AE98C(&y2, &x2, text, ptrSecondFontTableLarge, ptrFirstFontTableLarge, 0);
    x = 0x37;
    y = 0xA7;
    DL = write_text_at_abs_coord(DL, &x, &y, text, ptrSecondFontTableLarge, ptrFirstFontTableLarge, 0xFF, viGetX(), viGetY(), 0, 0);

    if (g_isBondKIA)
    {
        text = langGet(TEXT(LTITLE, 0x64)); // KILLED IN ACTION*
        phi_v1 = 0x78000000 | 0xFF;
    }
    else if (mission_failed_or_aborted)
    {
        text = langGet(TEXT(LTITLE, 0x65)); // ABORTED*
        phi_v1 = 0x78000000 | 0xFF;
    }
    else if (frontCompleteAllObjectivesAliveSuccess())
    {

        text = langGet(TEXT(LTITLE, 0x66)); // Completed*
        phi_v1 = 0xFF;
    }
    else
    {
        text = langGet(TEXT(LTITLE, 0x67)); // FAILED*
        phi_v1 = 0x78000000 | 0xFF;
    }

    x = x2 + 0x37;
    y = 0xA7;
    DL = write_text_at_abs_coord(DL, &x, &y, text, ptrSecondFontTableLarge, ptrFirstFontTableLarge, phi_v1, viGetX(), viGetY(), 0, 0);

    DL = print_objectives_and_status_to_menu(DL, 0xBF, &stagename, 1);
    DL = add_tab2_next(DL);
    DL = add_tab3_previous(DL);
    DL = load_draw_selected_icon_folder_select(DL);
    return DL;
}

